INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2017-06-11 21:32:58', '2017-06-12 00:32:58', 'Bem-vindo ao WordPress. Esse é o seu primeiro post. Edite-o ou exclua-o, e então comece a escrever!', 'Olá, mundo!', '', 'trash', 'open', 'open', '', 'ola-mundo__trashed', '', '', '2017-06-11 22:09:28', '2017-06-12 01:09:28', '', 0, 'http://homephoeniix.com.br/?p=1', 0, 'post', '', 1),
(2, 1, '2017-06-11 21:32:58', '2017-06-12 00:32:58', 'Este é o exemplo de uma página. É diferente de um post de blog porque é estática e pode aparecer em menus de navegação (na maioria dos temas). A maioria das pessoas começam com uma página \'Sobre\' que as apresenta aos potenciais visitantes do site. Você pode usar algo como:\n\n<blockquote>Oi! Sou um estudante de Biologia e gosto de esportes e natureza. Nos fins-de-semana pratico futebol com meus amigos no clube local. Eu moro em Valinhos e fiz este site para falar sobre minha cidade.</blockquote>\n\n...ou algo como:\n\n<blockquote>A empresa Logos foi fundada em 1980, e tem provido o comércio local com o que há de melhor em informatização. Localizada em Recife, nossa empresa tem se destacado como um das que também contribuem para o descarte correto de equipamentos eletrônicos substituídos.</blockquote>\n\nComo um novo usuário WordPress, vá ao seu <a href=\"http://homephoeniix.com.br/wp-admin/\">Painel</a> para excluir este conteúdo e criar o seu. Divirta-se!', 'Página de exemplo', '', 'trash', 'closed', 'open', '', 'pagina-exemplo__trashed', '', '', '2017-06-11 21:54:36', '2017-06-12 00:54:36', '', 0, 'http://homephoeniix.com.br/?page_id=2', 0, 'page', '', 0),
(145, 2, '2017-06-22 01:22:34', '0000-00-00 00:00:00', '{\n    \"sidebars_widgets[spacious_header_sidebar]\": {\n        \"value\": [\n            \"sfsi-widget-4\"\n        ],\n        \"type\": \"option\",\n        \"user_id\": 2\n    },\n    \"widget_sfsi-widget[4]\": {\n        \"value\": {\n            \"encoded_serialized_instance\": \"YToyOntzOjU6InNob3dmIjtpOjE7czo1OiJ0aXRsZSI7czoxNjoiRW50cmUgZW0gY29udGF0byI7fQ==\",\n            \"title\": \"Entre em contato\",\n            \"is_widget_customizer_js_value\": true,\n            \"instance_hash_key\": \"fcb65b1b8784ab33f310defa236982db\"\n        },\n        \"type\": \"option\",\n        \"user_id\": 2\n    }\n}', '', '', 'auto-draft', 'closed', 'closed', '', '7aaa0af7-a614-4e76-9559-56c58c7e8ff8', '', '', '2017-06-22 01:22:34', '0000-00-00 00:00:00', '', 0, 'http://homephoeniix.com.br/?p=145', 0, 'customize_changeset', '', 0),
